    }

    return (
        <>
            <div>
                {/* <Avatar
                    onClick={onClickProfile}
                    style={{
                        backgroundColor: color,
                    }}
                    size="large"
                    icon={<UserOutlined />}
                >
                   
                </Avatar> */}
            </div>
        </>
    )
}

export default Profile;
