const dummyPost = {
    id: 2,
    User: {
        id: 1,
        nickname: '제로초',
    },
    content: '나는 더미입니다.',
    comments: [],
};

const dummyComment = {
    id: 1,
    User: {
        id: 1,
        nickname: '포로리',
    },
    createdAt: new Date(),
    content: '더미 댓글입니다.',
};


export const initialState = {
    allPosts: [{
        id: 1,
        title: '첫번째 글',
        kategorie: null,
        User: {
            id: 1,
            nickname: '보노보노'
        },
        content: '',
        img: '',
        comments: [],
    },
    {
        id: 2,
        title: '두번째 글',
